import React, {Component} from 'react';
import './Home.css';
import {checkStatus, fixValues} from './Backend';

class Home extends Component {
  state = {
    balance: 0
  }

  componentDidMount() {
    const mintButton = document.getElementsByClassName('mint')[0];
    const burnButton = document.getElementsByClassName('burn')[0];
    const connectButton = document.getElementsByClassName('connect')[0];
    const amount = Number(document.getElementsByClassName("amount")[0].value);

    const wallet = Number(document.getElementsByClassName("wallet")[0].value);
    const balance = Number(document.getElementsByClassName("balance")[0].value);
    const circulation = Number(document.getElementsByClassName("circulation")[0].value);

    checkStatus(mintButton, burnButton, connectButton, amount);
    fixValues(wallet, balance, circulation)
  }

  handleChange = (event) => {
    checkStatus(document.getElementsByClassName('mint')[0], Number(event.target.value));
    this.setState({state: event.target.value});
  }

  render() {
    return (
      <div className="main-card">
        <div className="connect" onClick={this.changeVisibility}> Connect Wallet </div>
        <div className="top-div">
          <div className="wallet"> Wallet:  </div>
          <div className="balance"> Balance: 0 MC</div>
        </div>
        <div className="bottom-div">
          <div className="circulation"> Circulation: 0</div>
          <div className="right-div">
            <input className="amount" type="text" onChange={this.handleChange} contentEditable="true" defaultValue="0"></input>
            <div className="buttons">
                <div className="mint"> mint </div>
                <div className="burn"> burn </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
