import React, {Component} from 'react';
import Navbar from './Navbar';
import './Projects.css';
import $ from 'jquery'; 

class Projects extends Component {
    constructor(props) {
        super(props);
        this.myFilter = this.myFilter.bind(this);
    }

    myFilter(param) {
        document.querySelector('.current').classList.remove('selected');
        switch(param) {
            case "all":
                document.getElementById('allProjects').classList.add('current');
                break;
            case "personal":
                document.getElementById('nav-projects').classList.add('current');
                break;
            case "work":
                document.getElementById('nav-work').classList.add('current');
                break;
            case "coursework":
                document.getElementById('nav-work').classList.add('current');
                break;
            default:
                break;
        }
        $('.hide').removeClass('hide');
        switch(param) {
            case "all":

                break;
            case "personal":

                break;
            case "work":

                break;
            case "coursework":

                break;
            default:
                break;
        }
    }

    render() {
        return (
            <div> 
                <Navbar page="projects"/>
                <div className="projectsPage">
                    <div className="projectsTop">
                        <h1 className="projectsHeader"> PROJECTS </h1> 
                        <div className="projectsFilter">
                            <div id="allProjects" className="filterButton selected" onClick={() => this.myFilter("all")}> all projects </div>
                            <div id="personalProjects" className="filterButton" onClick={() => this.myFilter("personal")}> personal </div>
                            <div id="workProjects" className="filterButton" onClick={() => this.myFilter("work")}> work </div>
                            <div id="courseProjects" className="filterButton" onClick={() => this.myFilter("coursework")}> coursework </div>
                        </div>
                    </div>
                </div>    
            </div>
        );
    }
}

export default Projects;
