import React, {Component} from 'react';
import './Home.css';
import {checkStatus, returnCount} from './Mint';

class Home extends Component {
  render() {
    return (
      <div className="main-card">
        <div className="enter-button" onClick={this.changeVisibility}> Connect Wallet </div>
        <div className="top-div">
          <div className="wallet"> Wallet:  </div>
          <div className="MC-balance"> MarcCoin Balance: 0 </div>
        </div>
        <div className="bottom-div">
          <div className="bank-balance"> Bank Balance: 0</div>
          <div className="right-div">
            <input className="amount" type="text" onChange={this.handleChange} contentEditable="true" defaultValue="0"></input>
            <div className="buttons">
                <div className="mint"> mint </div>
                <div className="burn"> burn </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Home;
